                INOUT ioNUMFIELD NUM(11) DEFAULT 16,
                INOUT ioBINARYFIELD BINARY(100) DEFAULT 'a',
                INOUT ioVARBINARYFIELD VARBINARY(100) DEFAULT 'b',
                INOUT ioBLOBFIELD BLOB DEFAULT 'c',
