CREATE OR REPLACE PROCEDURE SUMITG1.SPCHAR (
                IN CHARFIELD CHAR(10) DEFAULT '',
                IN VARCHARFIELD VARCHAR(20) DEFAULT '',
                IN CLOBFIELD CLOB DEFAULT '',
                INOUT ioCHARFIELD CHAR(10) DEFAULT '',
                INOUT ioVARCHARFIELD VARCHAR(20) DEFAULT '',
                INOUT ioCLOBFIELD CLOB DEFAULT '',               
                OUT oCHARFIELD CHAR(10),
                OUT oVARCHARFIELD VARCHAR(20),
                OUT oCLOBFIELD CLOB
            )
        LANGUAGE SQL
        SPECIFIC SUMITG1.SPCHAR
        NOT DETERMINISTIC
        MODIFIES SQL DATA
        DYNAMIC RESULT SETS 1
        CALLED ON NULL INPUT
PROCBODY : BEGIN
DECLARE C1 CURSOR WITH RETURN FOR SELECT * FROM SUMITG1 . QRPGLESRC ;

SET OCHARFIELD = IOCHARFIELD ;
SET OVARCHARFIELD = IOVARCHARFIELD ;
SET OCLOBFIELD = '{"a":"b"}' ;

SET IOCHARFIELD = CHARFIELD ;
SET IOVARCHARFIELD = VARCHARFIELD ;
--SET IOCLOBFIELD = CLOBFIELD ;

OPEN C1 ;
RETURN ;
END PROCBODY 


call SUMITG1.SPCHAR(CHARFIELD=>'a',VARCHARFIELD=>'b', CLOBFIELD=>'c',ioCHARFIELD=>'d',ioVARCHARFIELD=>'e',ioCLOBFIELD=>'f',oCHARFIELD=>?,oVARCHARFIELD=>?,oCLOBFIELD=>?)

