CREATE OR REPLACE PROCEDURE SUMITG1.SPDATE (
                IN TIMEFIELD TIME DEFAULT CURRENT TIME,
                IN TIMSTAMPFIELD TIMESTAMP DEFAULT CURRENT TIMESTAMP,
                IN DATEFIELD DATE DEFAULT CURRENT DATE,
                
                INOUT ioTIMEFIELD TIME DEFAULT CURRENT TIME,
               INOUT ioTIMSTAMPFIELD TIMESTAMP DEFAULT CURRENT TIMESTAMP,
                INOUT ioDATEFIELD DATE DEFAULT CURRENT DATE,
                 
                OUT oTIMEFIELD TIME,
                OUT oTIMSTAMPFIELD TIMESTAMP,
                OUT oDATEFIELD DATE
                
            )
      
        LANGUAGE SQL
        SPECIFIC SUMITG1.DTPROC4
        NOT DETERMINISTIC
        MODIFIES SQL DATA
        CALLED ON NULL INPUT
        SET OPTION DBGVIEW=*SOURCE
        
PROCBODY: BEGIN
    SET OTIMEFIELD = TIMEFIELD;
   SET OTIMSTAMPFIELD = TIMSTAMPFIELD;
   SET ODATEFIELD = DATEFIELD;
    SET IOTIMEFIELD = TIMEFIELD;
    SET IOTIMSTAMPFIELD = TIMSTAMPFIELD;
    SET IODATEFIELD = DATEFIELD;
     

  
 
    RETURN;
END PROCBODY

;

