CREATE OR REPLACE PROCEDURE SUMITG1.SPFLOAT (
              IN FLOATFIELD FLOAT DEFAULT 10.12,
                IN REALFIELD REAL DEFAULT 11.13,
                IN DOUBLEFIELD DOUBLE DEFAULT 12.14,
                
                    INOUT ioFLOATFIELD FLOAT DEFAULT 100.12,
                INOUT ioREALFIELD REAL DEFAULT 111.13,
                INOUT ioDOUBLEFIELD DOUBLE DEFAULT 112.14,
                      OUT oFLOATFIELD FLOAT,
                OUT oREALFIELD REAL,
                OUT oDOUBLEFIELD DOUBLE
                
            )
         
        LANGUAGE SQL
        SPECIFIC SUMITG1.SPFLOAT
        NOT DETERMINISTIC
        MODIFIES SQL DATA
        CALLED ON NULL INPUT
PROCBODY: BEGIN
 set oFLOATFIELD = ioFLOATFIELD;
  set oREALFIELD = ioREALFIELD;
   set oDOUBLEFIELD = ioDOUBLEFIELD;
   
    set ioFLOATFIELD = FLOATFIELD;
     set ioREALFIELD = REALFIELD;
      set ioDOUBLEFIELD = DOUBLEFIELD;
    
    RETURN;
END PROCBODY

;

