CREATE OR REPLACE PROCEDURE SUMITG1.SPPARM (
                IN QHTTP_CLIENT_IP CHAR(20) DEFAULT '',
                IN QHTTP_METHOD VARCHAR(20) DEFAULT '',
                IN QHTTP_USER VARCHAR(20) DEFAULT '',
                IN QHTTP_SERVER VARCHAR(20) DEFAULT '',
                IN QHTTP_SERVER_USER VARCHAR(20),
                out oQHTTP_CLIENT_IP CHAR(20) ,
                out oQHTTP_METHOD VARCHAR(20) ,
                out oQHTTP_USER VARCHAR(20) ,
                out oQHTTP_SERVER VARCHAR(20) ,
                out oQHTTP_SERVER_USER VARCHAR(20) ,
                OUT QHTTP_STATUS_CODE int
               
            )
        LANGUAGE SQL
        SPECIFIC SUMITG1.SPPARM
        NOT DETERMINISTIC
        MODIFIES SQL DATA
        
        CALLED ON NULL INPUT
PROCBODY : BEGIN
 

SET oQHTTP_CLIENT_IP = QHTTP_CLIENT_IP ;
 SET oQHTTP_METHOD = QHTTP_METHOD ;
 SET oQHTTP_USER = QHTTP_USER ;
 SET oQHTTP_SERVER = QHTTP_SERVER ;
 SET oQHTTP_SERVER_USER = QHTTP_SERVER_USER ;
 set QHTTP_STATUS_CODE = 404;
 
RETURN ;
END PROCBODY 


