CREATE OR REPLACE PROCEDURE SUMITG1.SPRS (
                IN CHARFIELD CHAR(10) DEFAULT '',
                 
                INOUT ioCHARFIELD CHAR(10) DEFAULT '',
                
                OUT oCHARFIELD CHAR(10) 
            )
        LANGUAGE SQL
        SPECIFIC SUMITG1.SPRS
        NOT DETERMINISTIC
        MODIFIES SQL DATA
        DYNAMIC RESULT SETS 2
        CALLED ON NULL INPUT
PROCBODY : BEGIN
DECLARE C1 CURSOR WITH RETURN FOR SELECT * FROM SUMITG1 . QRPGLESRC ;
DECLARE C2 CURSOR WITH RETURN FOR sELECT * FROM SUMITG1 . qddssrc ;

SET OCHARFIELD = IOCHARFIELD ;
 

SET IOCHARFIELD = CHARFIELD ;
 

OPEN C1 ;
OPEN C2 ;
RETURN ;
END PROCBODY 