CREATE OR REPLACE PROCEDURE SUMITG1.SPBIN (
                
                IN BINARYFIELD BINARY(100) DEFAULT 'a',
                IN VARBINARYFIELD VARBINARY(100) DEFAULT 'b',
                INOUT ioBINARYFIELD BINARY(100) DEFAULT 'a',
                INOUT ioVARBINARYFIELD VARBINARY(100) DEFAULT 'b',
                OUT oBINARYFIELD BINARY(100),
                OUT oVARBINARYFIELD VARBINARY(100)
            )
         
        LANGUAGE SQL
        SPECIFIC SUMITG1.SPBIN
        NOT DETERMINISTIC
        MODIFIES SQL DATA
        CALLED ON NULL INPUT
PROCBODY: BEGIN
    SET OBINARYFIELD = IOBINARYFIELD;
    SET OVARBINARYFIELD = IOVARBINARYFIELD;
    SET IOBINARYFIELD = BINARYFIELD;
    SET IOVARBINARYFIELD = VARBINARYFIELD;
    
    RETURN;
END PROCBODY

;




